    autoCreateViewport: false, // prevent viewport creation

    //(app_requires)

    //(app_controller_1)

    //(app_onbeforelaunch)

    /**
     * Launch application and create viewport
     */
    launch: function () {
        Ext.create('EED.view.Viewport');
    }
});

