Ext.define('EED.controller.Lieferant', {
    extend: 'Ext.app.Controller',

    models: ['Lieferant'],
    stores: ['Lieferanten'],

    //(controllerlieferant_refs)

    /**
     * Launch Methode des Controllers.
     */
    onLaunch: function () {
        // Laden der Lieferanten
        this.getLieferantenStore().load();

        //(controllerlieferant_control)
    }//(controllerlieferant_more)

});

refs: [
    {
        ref: 'lieferantengridDeleteButton',
        selector: '#lieferant-lieferantengrid button[action=lieferant-remove]'
    },
    {
        ref: 'lieferantengrid',
        selector: '#lieferant-lieferantengrid'
    }
],

this.control({
    '#lieferant-lieferantengrid button[action=lieferant-add]': {
        click: this.addLieferant
    },
    '#lieferant-lieferantengrid button[action=lieferant-remove]': {
        click: this.removeLieferant
    },
    '#lieferant-lieferantengrid': {
        selectionchange: this.selectLieferant
    }
});

,

/**
 * Neuen Lieferanten hinzufügen.
 */
addLieferant: function () {
    var store = this.getLieferantenStore(),
        rowediting = this.getLieferantengrid().getPlugin('rowediting');

    rowediting.cancelEdit(); // evtl. bestehenden Edit Prozess beenden

    // Neuen Lieferant erzeugen
    var r = Ext.create('EED.model.Lieferant');

    store.insert(0, r); // Lieferant an erster Stelle hinzufügen
    rowediting.startEdit(0, 0); // Edit Prozess für neuen Datensatz starten
},

/**
 * Lieferanten löschen.
 */
removeLieferant: function () {
    var sm = this.getLieferantengrid().getSelectionModel(),
        store = this.getLieferantenStore(),
        rowediting = this.getLieferantengrid().getPlugin('rowediting');

    rowediting.cancelEdit();
    store.remove(sm.getSelection());
    if (store.getCount() > 0) {
        sm.select(0);
    }
},

/**
 * Selektion eines Mandanten aktiviert oder deaktiviert den delete Button.
 *
 * @param {Ext.selection.Model} selModel SelectionModel des Grids.
 * @param {EED.model.Lieferant[]} records Liste ausgwählter Lieferanten.
 */
selectLieferant: function (selModel, records) {
    this.getLieferantengridDeleteButton().setDisabled(!records.length);
}

Ext.define('EED.controller.Bestellung', {
    extend: 'Ext.app.Controller',

    models: ['Bestellung'],
    stores: ['Bestellungen'],

    refs: [
        {
            ref: 'bestellungForm',
            selector: 'bestellung-bestellungformular'
        }
    ],

    /**
     * Launch Methode des Controllers.
     */
    onLaunch: function () {
        // Laden der Bestellungen
        this.getBestellungenStore().load();

        //(controllerbestellung_control)

        // Formular initialisieren
        this.createEmptyRecordAndBindToForm();
    },

    //(controllerbestellung_create)

    //(controllerbestellung_load)

    //(controllerbetellung_remove)

    //(controllerbestellung_save)

    //(controllerbestellung_update)
});

this.control({
    //(controllerbestellung_control_2)
    'bestellung-bestellungformular button[action=bestellungsave]': {
        click: this.saveBestellung
    }
});

/**
 * Erstellt ein leeres <i>Bestellung</i> Model und verknüpft es mit dem Formular.
 */
createEmptyRecordAndBindToForm: function () {
    var newRecord = Ext.create('EED.model.Bestellung');
    this.loadFormWithRecord(newRecord);
},

/**
 * Verknüpft das Formular mit dem übergebenen Model und resetted die Validierung.
 *
 * @param {EED.model.Bestellung} record Bestellung Model
 */
loadFormWithRecord: function (record) {
    var form = this.getBestellungForm().getForm();
    form.loadRecord(record);
    form.clearInvalid();
},

/**
 * Entfernt eine Bestellung aus dem Store.
 *
 * @param {Ext.grid.Panel} grid GridPanel
 * @param {Number} rowIndex Index des ausgewählten Eintrages.
 */
removeBestellung: function (grid, rowIndex) {
    grid.getStore().removeAt(rowIndex);
    var form = this.getBestellungForm().getForm();
    form.clearInvalid();
},

/**
 * Speichert eine Bestellung.
 */
saveBestellung: function () {
    var form = this.getBestellungForm().getForm(),
        record = form.getRecord();

    form.updateRecord(record);

    // TODO: look for getter
    if (record.phantom === true) { // neuer Eintrag
        this.getBestellungenStore().add(record);
    } else {
        // update is a custom overwrite!
        this.getBestellungenStore().update(record);
    }

    form.reset();
    this.createEmptyRecordAndBindToForm();
},

/**
 * Ausgewählten Eintrag in das Formular laden um ihn zu aktualisieren.
 *
 * @param {Ext.grid.View} view GridPanel View.
 * @param {EED.model.Bestellung} record Ausgewähltes Bestellung Model aus dem Grid.
 */
updateBestellung: function (view, record) {
    this.loadFormWithRecord(record);
}

'bestellung-bestellunggrid': {
    'bestellungdelete': this.removeBestellung
},
'bestellung-bestellunggrid gridview': {
    'itemdblclick': this.updateBestellung
},
